"""Op Code Product Model."""

from sqlalchemy import Column, DateTime, Integer, String, ForeignKey
from typing import Dict, Any
from appt_orm.session_config import BaseForModels

class OpCodeProduct(BaseForModels):  # type: ignore
    """Op Code Product Model."""

    __tablename__ = "appt_op_code_product"

    id = Column(Integer, primary_key=True, autoincrement=True)
    product_id = Column(Integer, ForeignKey("appt_product.id"))
    op_code = Column(String)
    op_code_description = Column(String)
    db_update_role = Column(String)
    db_creation_date = Column(DateTime)
    db_update_date = Column(DateTime)


    def as_dict(self) -> Dict[str, Any]:
        """Return attributes of the keys in the table."""
        return {
            key.name: getattr(self, key.name)
            for key in self.__table__.columns
        }
		