"""Consumer Model."""

from sqlalchemy import Column, ForeignKey, DateTime, Integer, String
from typing import Dict, Any
from appt_orm.session_config import BaseForModels


class Consumer(BaseForModels):  # type: ignore
    """Consumer Model."""

    __tablename__ = "appt_consumer"

    id = Column(Integer, primary_key=True, autoincrement=True)
    dealer_integration_partner_id = Column(Integer, ForeignKey("appt_dealer_integration_partner.id"))
    integration_consumer_id = Column(String)
    product_consumer_id = Column(String)
    first_name = Column(String)
    last_name = Column(String)
    email_address = Column(String)
    phone_number = Column(String)
    db_update_role = Column(String)
    db_creation_date = Column(DateTime)
    db_update_date = Column(DateTime)

    def as_dict(self) -> Dict[str, Any]:
        """Return attributes of the keys in the table."""
        return {
            key.name: getattr(self, key.name)
            for key in self.__table__.columns
        }
