"""ConsumerProfile Model."""

from sqlalchemy import Column, DateTime, Integer, String
from typing import Dict, Any
from cdpi_orm.session_config import BaseForModels


class ConsumerProfile(BaseForModels):  # type: ignore
    """ConsumerProfile Model."""

    __tablename__ = "cdpi_consumer_profile"

    id = Column(Integer, primary_key=True, autoincrement=True)
    cdp_master_consumer_id = Column(String)
    cdp_dealer_consumer_id = Column(String)
    data_load_date = Column(DateTime)
    master_pscore_sales = Column(String)
    master_pscore_service = Column(String)
    dealer_pscore_sales = Column(String)
    dealer_pscore_service = Column(String)
    prev_master_pscore_sales = Column(String)
    prev_master_pscore_service = Column(String)
    prev_dealer_pscore_sales = Column(String)
    prev_dealer_pscore_service = Column(String)

    db_update_date = Column(DateTime)
    db_creation_date = Column(DateTime)

    def as_dict(self) -> Dict[str, Any]:
        """Return attributes of the keys in the table."""
        return {
            key.name: getattr(self, key.name)
            for key in self.__table__.columns
        }
