"""Dealer Model."""
from sqlalchemy.orm import relationship
from sqlalchemy import Column, DateTime, Integer, String
from typing import Dict, Any
from cdpi_orm.session_config import BaseForModels


class Dealer(BaseForModels):  # type: ignore
    """Dealer Model."""

    __tablename__ = "cdpi_dealer"

    id = Column(Integer, primary_key=True, autoincrement=True)
    dealer_name = Column(String)
    salesai_dealer_id = Column(String)
    serviceai_dealer_id = Column(String)
    sfdc_account_id = Column(String, unique=True)

    db_update_date = Column(DateTime)
    db_creation_date = Column(DateTime)

    def as_dict(self) -> Dict[str, Any]:
        """Return attributes of the keys in the table."""
        return {
            key.name: getattr(self, key.name)
            for key in self.__table__.columns
        }
